#!/bin/bash
# get gene of which AED is less than 0.5 from gff3 which is generated by maker3
name="$1" # genome name

gff3_merge -d ${name}.soft.mask_master_datastore_index.log -g -n -o ${name}.AED.gff3  # this perl scripts is wrapped in maker3
quality_filter.pl -a 0.5 ${name}.AED.gff3 > ${name}.0.5.gff3  # this perl scripts is wrapped in maker3
cat ${name}.0.5.gff3 |grep -v '#'|awk '$3=="gene"'|wc -l
